(* Handle base constructor *)
constructor.init.FBs := init.FBs;
constructor.init.PositionErrorDelay := init.PositionErrorDelay;
constructor.io_in.Activated := io_in.Activated;
constructor.io_in.Deactivated := io_in.Deactivated;
constructor.io_in.Tripped := io_in.Tripped;

constructor();

(* Control location *)
(* Auto/Man setting should be rtrig or non holding button *)
IF HMI_IN.SetToAuto AND NOT HMI_IN.SetToMan THEN
	vControlPosition := 1;
ELSIF HMI_IN.SetToMan THEN
	vControlPosition := 0;
END_IF

IF NOT(OpenFail.Q OR CloseFail.Q) THEN
	IF vControlPosition = 1 THEN
		vDeactivateCmd := (vDeactivateCmd OR HMI_IN.DeactivateCmd) AND NOT constructor.API.Deactivated;
		vActivateCmd := (vActivateCmd OR HMI_IN.ActivateCmd) AND NOT constructor.API.Activated;
	ELSIF vControlPosition = 0 THEN
		vDeactivateCmd := (vDeactivateCmd OR API_IN.DeactivateCmd) AND NOT constructor.API.Deactivated;
		vActivateCmd := (vActivateCmd OR API_IN.ActivateCmd) AND NOT constructor.API.Activated;
	ELSE
		vDeactivateCmd := FALSE;
		vActivateCmd := FALSE;	
	END_IF
ELSE
		vDeactivateCmd := FALSE;
		vActivateCmd := FALSE;		
END_IF

OpenFail(IN:= vDeactivateCmd AND NOT constructor.API.Deactivated, PT:= init.OpenFailDelay);
CloseFail(IN:= vActivateCmd AND NOT constructor.API.Activated, PT:= init.CloseFailDelay);

io_out.DeactivateCmd := vDeactivateCmd AND NOT (OpenFail.Q OR constructor.API.Deactivated OR constructor.API.PositionError);
io_out.ActivateCmd := vActivateCmd AND NOT (CloseFail.Q OR constructor.API.Activated OR constructor.API.PositionError);

API_OUT.Status[1] := BOOL_TO_DINT(constructor.API.Deactivated) + 2*BOOL_TO_DINT(constructor.API.Activated);
API_OUT.Status[2] := BOOL_TO_DINT(OpenFail.Q);
API_OUT.Status[3] := BOOL_TO_DINT(CloseFail.Q);
API_OUT.Status[4] := BOOL_TO_DINT(constructor.API.PositionError);
API_OUT.Status[5] := vControlPosition;
API_OUT.Status[6] := BOOL_TO_DINT(io_out.DeactivateCmd);
API_OUT.Status[7] := BOOL_TO_DINT(io_out.ActivateCmd);

IF API_OUT.Status[1] = 1 THEN
	API_OUT.Icon := 'Valve1';
ELSIF API_OUT.Status[1] = 2 THEN
	API_OUT.Icon := 'Valve2';
ELSE
	API_OUT.Icon := 'Valve3';
END_IF

(* Handle IOs depending on init. *)
IF init.FBs.0 AND init.FBs.1 THEN
	API.Activated := io_in.Activated;
	API.Deactivated := io_in.Deactivated;
ELSIF init.FBs.0 THEN
	API.Activated := NOT io_in.Deactivated;
	API.Deactivated := io_in.Deactivated;
ELSIF init.FBs.1 THEN
	API.Activated := io_in.Activated;
	API.Deactivated := NOT io_in.Activated;
ELSE
	(* Invalid configuration *)
END_IF;

(* Alarm management *)
(* Position error *)
PositionErrorDelay(
	IN:= API.Activated AND API.Deactivated OR NOT (API.Activated OR API.Deactivated),
	PT:= init.PositionErrorDelay,
	Q  => API.PositionError);

(* Communicate status to HMI *)	
HMI := API;

(* Description *)
(*Configure subclasses*)
DrainValve.init := Init.DrainValve;
FillingValve.init := Init.FillingValve;

(*Map inputs*)
DrainValve.io_in := IO_IN.DrainValve;
FillingValve.io_in := IO_IN.FillingValve;

(*Call the subclasses*)
DrainValve();
FillingValve();

(* Implement logic *)
API_OUT.TankLevel_pc := IO_IN.Level_cbm;

(*Map outputs*)
IO_OUT.DrainValve := DrainValve.io_out;
IO_OUT.FillingValve := FillingValve.io_out;

IF ForwardCMD AND NOT (Position >= 180) THEN
	Position := Position + (cfgOpenSpeed_s/cfgTaskSeq_ms);
ELSIF BackwardCMD AND NOT (Position <= -180) THEN
	Position := Position - (cfgCloseSpeed_s/cfgTaskSeq_ms);	
END_IF

IF OpenCMD AND NOT (Position > 100) THEN
	Position := Position + (cfgOpenSpeed_s/cfgTaskSeq_ms);
ELSIF CloseCMD AND NOT (Position <= 0) THEN
	Position := Position - (cfgCloseSpeed_s/cfgTaskSeq_ms);	
END_IF

Status[0] := CBActivated;;

vApparentPower_kVA := cfgNominalPower/(3*cfgPowerFactor);

vPhaseCurrent := (vApparentPower_kVA*1000)/LineVoltage;

CurrentL1 := (vPhaseCurrent/SQRT(3)+(BOOL_TO_REAL(simShortCircuit)*250))*BOOL_TO_REAL(Status[0]);
CurrentL2 := (vPhaseCurrent/SQRT(3)+(BOOL_TO_REAL(simShortCircuit)*250))*BOOL_TO_REAL(Status[0]);
CurrentL3 := (vPhaseCurrent/SQRT(3)+(BOOL_TO_REAL(simShortCircuit)*250))*BOOL_TO_REAL(Status[0]);

vNs := ((120 *Frequency)/DINT_TO_REAL(cfgNoOfPoles))*BOOL_TO_REAL(Status[0]);

SpeedRamp(
	IN:= vNs, 
	ASCEND:= cfgSpeedUpRate, 
	DESCEND:= cfgSpeedDownRate, 
	TIMEBASE:= T#2S, 
	OUT=>Speed);

vBlinkTime := 100000/MAX(1,Speed);

SpeedBlink(ENABLE:= vBlinkTime < 100000, TIMELOW:= REAL_TO_TIME(vBlinkTime), TIMEHIGH:= REAL_TO_TIME(vBlinkTime), OUT=> Status[1]);

(* Description of action:
If the open/close signal comes the valve opens/closes in designated time *)

OpenTime(IN:= OpenCmd AND NOT Opened, PT:=cfgOpeningTime);
CloseTime(IN:= CloseCmd AND NOT Closed, PT:=cfgClosingTime);

IF Opened AND CloseCmd THEN
	Opened := FALSE;
ELSIF OpenTime.Q AND NOT simOpenFail THEN
	Opened := TRUE;
END_IF

IF Closed AND OpenCmd THEN
	Closed := FALSE;
ELSIF CloseTime.Q AND NOT simCloseFail THEN
	Closed := TRUE;
END_IF

PumpSR(SET1:= OnCmd AND NOT (OffCmd OR SimTrip OR RunStatus=1) , RESET:= (OffCmd OR SimTrip), Q1=>vPumpRunning );

RunStatus := BOOL_TO_DINT(vPumpRunning);

IF vPumpRunning AND NOT NoWater THEN
	vFlowmeterSP := BOOL_TO_REAL( NOT SimFilterFail) * cfgFlowrate;
	vAscend := cfgRampUpRate;
	vDescend := cfgRampDownRate;
ELSE
	vFlowmeterSP := 0;
	vAscend := 0;
	vDescend := 99999999;
END_IF


Flowmeter(
	IN:= vFlowmeterSP, 
	ASCEND:= vAscend,
	TIMEBASE:= T#500MS, 
	DESCEND:= vDescend, 
	OUT=> FlowmeterOut);

IF NOT vColdRestart THEN
	TankLevel_cbm := cfgStartLevel;
	vColdRestart := TRUE;
END_IF

vNominalVolume := cfgLenght * cfgWidth * cfgHeight;

vInflowTotal := Inflow1 + Inflow2 + Inflow3 + Inflow4 + Inflow5;

vOutflowTotal := Outflow1 + Outflow2 + Outflow3 + Outflow4 + Outflow5;

(* Task cycle speed increase will lower the speed of TankLevel filling *)
vDeltaTime := cfgTaskCycle/DINT_TO_REAL(3600 * 1000);

(* Tank level calculation, independent of task execution cycle *)
TankLevel_cbm :=LIMIT(0,TankLevel_cbm + (vInflowTotal - vOutflowTotal) * vDeltaTime, vNominalVolume);

TankLevel_pc := (TankLevel_cbm / vNominalVolume) * 100;

(* Limit switch handling *)
FSHHActive := TankLevel_pc >= cfgFSHH;
FSHActive := TankLevel_pc >= cfgFSH;
FSLActive := TankLevel_pc <= cfgFSL;
FSLLActive := TankLevel_pc <= cfgFSLL;

